namespace Tmdb\Model\Common\Trailer;

use Tmdb\Model\Common\AbstractTrailer;

/**
 * Class Youtube
 * @package Tmdb\Model\Common\Trailer
 */
class Youtube extends AbstractTrailer
{
    public const URL = 'http://www.youtube.com/watch?v=%s';
    public static $properties = [
        'name',
        'size',
        'source',
        'type'
    ];
    private $name;
    private $size;
    private $source;
    private $type;

    /**
     * Retrieve the url to the source
     *
     * @return string
     */
    public function getUrl()
    {
        return sprintf(self::URL, $this->source);
    }

    /**
     * @return string
     */
    public function getName()
    {
        return $this->name;
    }

    /**
     * @param string $name
     * @return self
     */
    public function setName($name)
    {
        $this->name = $name;

        return $this;
    }

    /**
     * @return string
     */
    public function getSize()
    {
        return $this->size;
    }

    /**
     * @param string $size
     * @return self
     */
    public function setSize($size)
    {
        $this->size = $size;

        return $this;
    }

    /**
     * @return string
     */
    public function getSource()
    {
        return $this->source;
    }

    /**
     * @param string $source
     * @return self
     */
    public function setSource($source)
    {
        $this->source = $source;

        return $this;
    }

    /**
     * @return string
     */
    public function getType()
    {
        return $this->type;
    }

    /**
     * @param string $type
     * @return self
     */
    public function setType($type)
    {
        $this->type = $type;

        return $this;
    }
}
